from helper.monoisotopic_mass_dict import MONOISOTOPIC_MASS_DICT


def calculating_protein_mass(protein_sequence):
    total_mass = 0.0
    for amino_acid in protein_sequence:
        total_mass += MONOISOTOPIC_MASS_DICT.get(amino_acid, 0.0)

    return total_mass


print(calculating_protein_mass(""))  # Example usage
