from helper.rna_codon_dict import RNA_CONDON_DICT, PROTEIN_TO_CODONS_DICT


def inferring_mRNA_from_protein(protein_sequence):
    mRNA_sequences = []
    total_posible_rna_sequences = 1
    for amino_acid in protein_sequence:
        if amino_acid not in PROTEIN_TO_CODONS_DICT:
            return f"Invalid amino acid '{amino_acid}' in protein sequence."
        total_posible_rna_sequences *= len(PROTEIN_TO_CODONS_DICT[amino_acid])

    total_posible_rna_sequences *= len(PROTEIN_TO_CODONS_DICT['Stop'])

    return total_posible_rna_sequences % 1000000


if __name__ == "__main__":
    protein_sequence = ""
    result = inferring_mRNA_from_protein(protein_sequence)
    print(result)
