from helper.rna_codon_dict import RNA_CONDON_DICT as rna_codon_dict


def translate_rna_to_protein(rna_sequence):
    protein_sequence = ""

    i = 0
    while i < len(rna_sequence) - 3:
        codon = rna_sequence[i:i+3]
        amino_acid = rna_codon_dict[codon]

        # Use this condition when START codon is required
        if amino_acid != "M" and len(protein_sequence) == 0:
            i += 1
            continue

        # Use this condition when STOP codon is required
        if amino_acid == "Stop":
            break

        protein_sequence += amino_acid
        i += 3

    return protein_sequence


if __name__ == "__main__":
    print(translate_rna_to_protein(
        ""))
